import React, {Component} from 'react';
import './Login.css';

class Login extends Component {
    constructor(props) {
        super(props);
        this.handleTextChange = this.handleTextChange.bind(this);
        this.handleButtonClick = this.handleButtonClick.bind(this);
        this.state = { 
            login: "",
            password: "",
            showRegister: false
        };
    }

    handleButtonClick() {
        console.log(this.state.login);
        console.log(this.state.password);
        const requestOptions = {
            method: 'POST',
            headers: {'Content-Type':'application/json'},
            body: JSON.stringify({"login": this.state.login, "password": this.state.password})
        };
        
        fetch('http://localhost:4000/login', requestOptions)
        .then(response => response.json())
        .then(data => {
            if (data.success) {
                // Handle successful login
            } else {
                // Handle login failure
            }
        });
    }

    handleTextChange(e) {
        this.setState({[e.target.name]: e.target.value});
    }

    toggleRegister = () => {
        this.setState({ showRegister: !this.state.showRegister });
    }

    render() {
        return (
            <div>
                <h1 id='title'>Login Page</h1>
                <div>
                    <input type="text" name="login" value={this.state.login} onChange={this.handleTextChange} placeholder="Login" />
                    <input type="password" name="password" value={this.state.password} onChange={this.handleTextChange} placeholder="Password" />
                    <input type="button" value="Login" onClick={this.handleButtonClick} />
                </div>
                <div>
                    <a href="#" onClick={this.toggleRegister}>I don't have an account</a>
                </div>
                {this.state.showRegister && <Register />}
            </div>
        );
    }
}

class Register extends Component {
    constructor(props) {
        super(props);
        this.handleTextChange = this.handleTextChange.bind(this);
        this.handleButtonClick = this.handleButtonClick.bind(this);
        this.state = { 
            login: "",
            password: ""
        };
    }

    handleButtonClick() {
        console.log(this.state.login);
        console.log(this.state.password);
        const requestOptions = {
            method: 'POST',
            headers: {'Content-Type':'application/json'},
            body: JSON.stringify({"login": this.state.login, "password": this.state.password})
        };
        
        fetch('http://localhost:4000/register', requestOptions)
        .then(response => response.json())
        .then(data => {
            if (data.success) {
                // Handle successful registration
            } else {
                // Handle registration failure
            }
        });
    }

    handleTextChange(e) {
        this.setState({[e.target.name]: e.target.value});
    }

    render() {
        return (
            <div>
                <h1 id='title'>Register Page</h1>
                <div>
                    <input type="text" name="login" value={this.state.login} onChange={this.handleTextChange} placeholder="Login" />
                    <input type="password" name="password" value={this.state.password} onChange={this.handleTextChange} placeholder="Password" />
                    <input type="button" value="Register" onClick={this.handleButtonClick} />
                </div>
            </div>
        );
    }
}

export default Login;
