import React, {Component} from 'react';
    import './Notes.css';
    import { ToastContainer,toast } from 'react-toastify';
    import 'react-toastify/dist/ReactToastify.css';
    class Notes extends Component {
     
        constructor(props) {
            super(props) //since we are extending class Table so we have to use super in order to override Component class constructor
            this.handleTextChange = this.handleTextChange.bind(this);
            this.handleButtonClick = this.handleButtonClick.bind(this);
            this.handleButtonClickDel = this.handleButtonClickDel.bind(this);
            this.handleButtonClickDelId = this.handleButtonClickDelId.bind(this);
            this.state = { 
               transactions: [],
               text_title: "",
               text_desc:"",
               id: "",
               owner: "defaultuser",
            }
         }

         componentDidMount() {
            this.populateData();
          }

        populateData(){
            this.fetch_retry('http://localhost:4000/transaction',3)
            .then(res => res.json())
            .then((data) => {
              this.setState({ transactions : data.result });
              console.log("state set");
              console.log(this.state.transactions);
            })
            .catch(console.log);
        }  

        async fetch_retry(url, n){
            try {
                return await fetch(url)
            } catch(err) {
                if (n === 1) throw err;
                await new Promise(resolve => setTimeout(resolve, 1000)); 
                return await this.fetch_retry(url, n - 1);
            }
        };


          renderTableData() {
            return this.state.transactions.map((transaction, index) => {
               const { id, title, description} = transaction //destructuring
               return (
                  <tr key={id}>
                     <td>{id}</td>
                     <td>{title}</td>
                     <td>{description}</td>
                  </tr>
               )
            })
         }

        handleButtonClickDel(){
           const requestOptions = {
               method: 'DELETE'
           }
           fetch('http://localhost:4000/transaction', requestOptions)
           .then(response => response.json())
           .then(data => this.populateData())

           this.setState({text_title : "", text_desc:"",transaction:[]});

        }
        handleButtonClickDelId(id) {
            const requestOptions = {
                method: 'DELETE',
                headers: {'Content-Type':'application/json'},
                body: JSON.stringify({"id" :this.state.id})
            };
            fetch(`http://localhost:4000/transaction/id/`, requestOptions)
                .then(response => response.json())
                .then(data => this.populateData());

            this.setState({ text_title: "", text_desc: "" , id: "" });
            this.setState(prevState => ({
                transactions: prevState.transactions.filter(transaction => transaction.id !== id)
            }));
        }

         handleButtonClick(){
             const userId = localStorage.getItem('UserId');
             const requestOptions = {
                 method: 'POST',
                 headers: {'Content-Type':'application/json'},
                 body: JSON.stringify({"title": this.state.text_title, "desc": this.state.text_desc, "owner": userId })
             };
             
             fetch('http://localhost:4000/transaction', requestOptions)
             .then(response => response.json())
             .then(data => this.populateData());
             
             this.setState({ text_title: "", text_desc: "" });
         }

         handleTextChange(e){
            this.setState({[e.target.name]:e.target.value})
         }

        render () {
        return (
            <div>
            <h1 id='title' style={{paddingRight:"1em"}}>Welcome to your Notes</h1><input style={{
                            float: "right", 
                            marginBottom: "1em", 
                            backgroundColor: "red", 
                            color: "white", 
                            border: "none", 
                            padding: "10px 20px", 
                            borderRadius: "5px", 
                            cursor: "pointer"
                        }}  type = "button" value ="Delete All" onClick={this.handleButtonClickDel} />
            <table id='transactions'>
               <tbody>
                   <tr>
                       <td>NÂ°</td>
                       <td>Title</td>
                       <td>Description</td>
                   </tr>
                   <tr>
                        <td><input type = "button" value ="ADD" onClick={this.handleButtonClick}/></td>
                        <td><input type="text" name ="text_title" value = {this.state.text_title} onChange={this.handleTextChange}/></td>
                        <td><input type="text" name = "text_desc" value = {this.state.text_desc} onChange={this.handleTextChange}/></td>
                        <td><input type="button" value="clear" style={{
                                    backgroundColor: "blue", 
                                    color: "white", 
                                    border: "none", 
                                    padding: "5px 10px", 
                                    borderRadius: "5px", 
                                    cursor: "pointer"
                                }} onClick={() => this.setState({ text_title: "", text_desc: "" })} /></td>
                   </tr>
                   
                  {  JSON.parse(localStorage.getItem('isLoggedIn'))
 && this.renderTableData()}
                  <tr>
                    <td colSpan="1"><input type="text" name="id" placeholder="     Number of the Note " value = {this.state.id} onChange={this.handleTextChange}></input></td>
                    <td><input type = "button" value ="remove note" style={{
                                    backgroundColor: "crimson", 
                                    color: "white", 
                                    border: "none", 
                                    padding: "5px 10px", 
                                    borderRadius: "5px", 
                                    cursor: "pointer"
                                }} /*onClick={this.handleButtonClickDelId(this.state.id)}*//></td>
                   </tr>
               </tbody>
            </table>
            <ToastContainer />
         </div>

        );
      }
    }

    export default Notes;
